mapper.update:
UPDATE USER_ADDRESS 
SET ADDRESS_ID = #{address_id}
, MEMBER_SERIAL = #{member_serial}
, ADDRESS = #{address}
, ADDRESS_DETAIL = #{address_detail}
, ZONECODE = #{zonecode}
WHERE MEMBER_SERIAL = #{member_serial}

mapper.select:
SELECT * FROM USER_ADDRESS
WHERE MEMBER_SERIAL = #{member_serial}
AND ADDRESS = #{address}
AND ADDRESS_DETAIL = #{address_detail}
AND ZONECODE = #{zonecode}
